	}

	///
	/// @brief Convert the chart object into xml string
	///
	/// @param[in] data 
	/// @return Text Xml string.
	///
	function cbMakeCharts ( data ) {
		if( data['renderType'] === 'pie' )
		{
			return officeChart.makePieChartStringFromData( data);
		}
		else if( data['renderType'] === 'column' )
    {
			return officeChart.makeColumnChartStringFromData( data);
    }
    else if( data['renderType'] === 'bar' )
    {
			return officeChart.makeBarChartStringFromData( data);
    }
    else if( data['renderType'] === 'group-bar' )
    {
			return officeChart.makeGroupBarChartStringFromData( data);
    }
	}
	
