            <c:barDir val="col"/>\
            <c:grouping val="clustered"/>'
            + seriesString + 
            '<c:axId val="45021824"/>\
            <c:axId val="45291008"/>\
        </c:barChart>\
        <c:catAx>\
            <c:axId val="45021824"/>\
            <c:scaling>\
                <c:orientation val="minMax"/>\
            </c:scaling>\
            <c:axPos val="b"/>\
            <c:numFmt formatCode="General" sourceLinked="1"/>\
            <c:tickLblPos val="nextTo"/>\
            <c:crossAx val="45291008"/>\
            <c:crosses val="autoZero"/>\
            <c:auto val="1"/>\
            <c:lblAlgn val="ctr"/>\
            <c:lblOffset val="100"/>\
        </c:catAx>\
        <c:valAx>\
            <c:axId val="45291008"/>\
            <c:scaling>\
                <c:orientation val="minMax"/>\
            </c:scaling>\
            <c:axPos val="l"/>\
            <c:majorGridlines/>\
            <c:numFmt formatCode="General" sourceLinked="1"/>\
            <c:tickLblPos val="nextTo"/>\
            <c:crossAx val="45021824"/>\
            <c:crosses val="autoZero"/>\
            <c:crossBetween val="between"/>\
        </c:valAx>\
    </c:plotArea>\
    <c:legend>\
        <c:legendPos val="r"/>\
